mapper.delete:
DELETE FROM tbl_like
WHERE member_id = #{memberId}
  AND post_id = #{diaryId}

mapper.select:
select vpd.id as post_id, vpd.created_datetime, vpd.updated_datetime, vpd.post_read_count, vpd.diary_like_count, vpd.diary_reply_count, vfps.file_path as diary_file_path, tm.member_name,
tm.id as member_id, tm.member_description, tm.social_img_url, tps.post_content, vfmf.file_path as member_file_path, tc.crew_name, tc.id as crew_id from tbl_member tm join view_post_diary vpd on tm.id = vpd.member_id join tbl_post_section tps on vpd.id = tps.post_id and vpd.post_status = 'active' and vpd.diary_secret = 'public' join view_file_post_section_file vfps on tps.id = vfps.post_section_id and vfps.image_type = 'main' left join view_file_member_file vfmf on vfmf.member_id = tm.id left join tbl_crew_diary tcd on tcd.diary_id = vpd.id left join tbl_crew tc on tcd.crew_id = tc.id
order by ${search.orderType} desc limit #{criteria.count} offset #{criteria.offset}

mapper.update:
update  tbl_diary
set diary_like_count = diary_like_count + #{diff}
where post_id = #{postId}

update tbl_diary
set diary_secret = #{secret}
where post_id = #{postId}

update tbl_diary
set diary_reply_count = diary_reply_count + #{count}
where post_id = #{postId}

mapper.select:
SELECT d.post_id AS postId, p.member_id AS memberId, p.post_title AS postTitle, ( SELECT s2.post_content FROM tbl_post_section s2 JOIN tbl_post_section_file psf2 ON s2.id = psf2.post_section_id WHERE s2.post_id = p.id AND psf2.image_type = 'main' ORDER BY s2.id ASC LIMIT 1 ) AS postContent, ( SELECT f.file_path FROM tbl_post_section s3 JOIN tbl_post_section_file psf3 ON s3.id = psf3.post_section_id JOIN tbl_file f ON psf3.file_id = f.id WHERE s3.post_id = p.id AND psf3.image_type = 'main' ORDER BY s3.id ASC LIMIT 1 ) AS mainImage,
p.created_datetime AS createdDatetime, p.updated_datetime AS updatedDatetime
FROM tbl_diary d JOIN tbl_post p ON d.post_id = p.id
WHERE p.member_id = #{memberId}
ORDER BY p.created_datetime DESC
LIMIT #{criteria.size} OFFSET #{criteria.offset}
